const fs = require('fs');
let giveawaysDue = [];
var timeNow = Math.floor(Date.now() / 1000);
var counter = 0;
var interval;
if (globalVars("giveawayRan") == null) {
    Actions.storeValue(1, 3, "giveawayRan", cache);

    var giveawaySysData = Actions.giveawaySystem;

    for (var key in giveawaySysData) {
        if (giveawaySysData.hasOwnProperty(key)) {
            let giveaways = giveawaySysData[key];
            let dueGive = giveaways.filter(({
                finishUnix
            }) => finishUnix <= timeNow);
            if (dueGive.length > 0) {
                dueGive.forEach(async f => {
                    giveawaysDue.push(f);
                    giveaways.splice(giveaways.findIndex(e => e.id === f.id), 1)
                });

                fs.writeFile("./data/giveawaySystem/giveawayData.json", JSON.stringify(giveawaySysData), function(err) {
                    if (err) {
                        return console.log(err);
                    }
                });
            }
        }
    }
    if (giveawaysDue.length > 0) {
        interval = setInterval(drawWinners, 2000);
    }

}

async function drawWinners() {
    var preCount = counter;
    var channelToFetch = client.channels.cache.get(giveawaysDue[preCount]["channel"]);
    if (channelToFetch != null) {
        channelToFetch.messages.fetch(giveawaysDue[preCount]["giveawayMessageID"]).then(giveawayMessage => {
            var giveawayEmojiCache = giveawayMessage.reactions.cache.get('ðŸŽ‰');
            giveawayEmojiCache.users.fetch().then(async function(giveawayUsers) {
                if (giveawayEmojiCache.count > 100) {
                    for (i = 0; i < giveawayEmojiCache.count; i++) {
                        var fetchedEmojiUsers = await giveawayEmojiCache.users.fetch({
                            after: giveawayUsers.last().id
                        });
                        await giveawayUsers.concat(fetchedEmojiUsers);
                    }
                }

                giveawayUsers = giveawayUsers.array();
                giveawayUsers = await giveawayUsers.filter(object => object.id != client.user.id);
                var numberOfWinners = parseInt(giveawaysDue[preCount]["winner"]);
                var gw = giveawayMessage.embeds[0];

                gw.color = 0x000000;
                if (gw.fields.length == 6) {
                    gw.fields[5].value = "Ended"
                }
                gw.description = "**Giveaway Ended**\n\n" + gw.description

                var winnerArray = [];
                var winnerText = "";
                if (giveawayUsers.length > 0) {
                    if (giveawayUsers.length > numberOfWinners) {
                        for (var i = 0; i < numberOfWinners; i++) {
                            var randomNumber = Math.floor(Math.random() * giveawayUsers.length);
                            winnerArray.push(`${giveawayUsers[randomNumber]}`);
                            giveawayUsers.splice(giveawayUsers.indexOf(randomNumber), randomNumber);
                        }
                    } else if (giveawayUsers.length <= numberOfWinners) {
                        for (i = 0; i < giveawayUsers.length; i++) {
                            winnerArray.push(`${giveawayUsers[i]}`);
                        }
                    }

                    for (i = 0; i < winnerArray.length; i++) {
                        winnerText = winnerText + winnerArray[i] + '\n';
                    }

                    gw.description += `\n\nThe winners are..\n${winnerText}`;
                    giveawayMessage.edit({
                        embed: gw
                    });
                    channelToFetch.send(`ðŸŽ‰ Congratulations! ðŸŽ‰ \n ${winnerText} \nThey have won **${giveawaysDue[preCount]["itemGivingAway"]}**`);

                } else {
                    gw.description += "\n\nNo one entered the giveaway and thus no one won.";
                    giveawayMessage.edit({
                        embed: gw
                    });
                    channelToFetch.send(`ðŸŽ‰ Giveaway Ended ðŸŽ‰\n\nNo one entered the giveaway and thus no one won.`);
                }
            })
        })
    }

    counter += 1;
    if (counter >= giveawaysDue.length) {
        clearInterval(interval);
    }
}

